
public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i1=100;
		System.out.println(i1);
		
		i1+=1;
		System.out.println(i1);
		
		i1-=2;
		System.out.println(i1);
		
		i1*=3;
		System.out.println(i1);
		
		i1/=4;
		System.out.println(i1);
	}

}
